import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { changeFileList } from 'webapp/art/reducer/art.reducer'

const ArtFile = ({ uuid, originalFileName }) => {

  const dispatch = useDispatch()

  const [artFile, setArtFile] = useState({
    uuid: uuid,
    originalFileName: originalFileName,
    refImg: false
  })

  const artFileChange = e => {
    e.stopPropagation()
    e.preventDefault()

    setArtFile({ ...ArtFile, [e.target.name]: e.target.value })
    console.log(artFile)

    dispatch(changeFileList({ uuid: e.target.getAttribute("data-uuid"), file: { ...artFile }}))
  }

  return (
    <>
      <div>
      <img src={`http://localhost:8080/art_files/display?fileName=${artFile.uuid + "_" + artFile.originalFileName}`} alt="" /><br />
      {artFile.fname}
      <button className="btn mt-10" style={{ marginLeft: "10px" }}
        value={artFile.repImg} name="repImg" data-uuid={artFile.uuid}
        >
        파일업로드 취소</button>
    </div>
      <label className="font-20px">작품 작업 날짜</label>
      <input className="mfp-title" data-uuid={artFile.uuid} type="text" name="fileWorkedDate" style={{ color: "black", wordWrap: "break-word" }}
        value={artFile.fileWorkedDate} placeholder="fileWorkedDate"
        onChange={(e) => artFileChange(e)} />
      <label className="font-20px">파일 제목</label>
      <input type="text" data-uuid={artFile.uuid} name="fileTitle" style={{ color: "black" }}
        value={artFile.fileTitle} placeholder="fileTitle"
        onChange={(e) => artFileChange(e)} />
      <label className="font-20px">파일 세부사항</label>
      <textarea name="fileDetail" data-uuid={artFile.uuid} style={{ color: "black", height: "200px" }}
        value={artFile.fileDetail} placeholder="fileDetail"
        onChange={(e) => artFileChange(e)} />
      {!artFile.repImg ? <>
        <label className="font-20px">대표이미지로 선택하시겠습니까?</label><br />
        <button className="btn btn-md btn-dark-outline btn-square mt-10"
          value={artFile.repImg} name="repImg" data-uuid={artFile.uuid}
          >
          Yes</button>
        <button className="btn btn-md btn-dark-outline btn-square mt-10"
          value={artFile.repImg} name="repImg"
          >
          NO</button> </> : <></>}</>
  )
}

export default ArtFile
