import React, { useEffect } from "react";
// DATA Files
import dataNavbar from "webapp/common/data/Navbar/main-navbar-data.json";
// Images
// Components
import HeaderOne from "webapp/common/Header/HeaderOne";
import PageTitleArt from "webapp/art/component/PageTitleArt";
import ArtContent from "webapp/art/component/Detail/ArtContent";
import FooterOne from "webapp/common/Footer/FooterOne";
import { useSelector, useDispatch } from "react-redux";
import { getArtRead } from "webapp/art/reducer/art.reducer";
import { useParams } from "react-router";

import { makeStyles } from '@material-ui/core/styles'
import Dialog from '@material-ui/core/Dialog'
import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar'
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import Slider from "react-slick";
import { Link } from 'react-router-dom'
import ArtSidebar from "./ArtSidebar";

const ArtDetail = () => {

  const useStyles = makeStyles((theme) => ({
    appBar: {
      position: 'relative',
      backgroundColor: "#561571",
      fontSize: 30,
    },
    title: {
      marginLeft: theme.spacing(3),
      flex: 1,
      color: "white",
      fontSize: 25,
      textAlign: "center"

    },
    closeSize: {
      fontSize: 30,
      color: "white"

    },
    dialogSize: {
      margin: 'auto auto',
      width: '95%',
      verticalAlign: "middle",
    },
  }));

  const settings = {
    dots: false,
    infinite: true,
    centerMode: true,
    autoplay: true,
    autoplaySpeed: 5000,
    slidesToShow: 1,
    slidesToScroll: 1,
    centerPadding: "0",
    className: "blog-grid-slider slick",
  };
  const Transition = React.forwardRef(function Transition(props, ref) {

    return <Slide direction="up" ref={ref} {...props} />;
  });

  const { id } = useParams()
  console.log(id)

  const art = useSelector(state => state.arts.art)
  console.log(art)

  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getArtRead(id))
  }, [])

  const fileList = art.files.map((file, i) => {
    console.log(file.uuid + "_" + file.fname)
    return (<>
      <img src={`http://localhost:8080/art_files/display/${file.uuid + "_" + file.fname}`} />
      <div>
        <p>{file.title}</p>
        <p>{file.fileDetail}</p>
      </div>
      <div className="post-metas">
        <div className="post-metas-center">
          <p className="post-date">{file.fileWorkedDate} </p>
        </div>
      </div>
    </>)
  })


  return (
    <>
      <HeaderOne data={dataNavbar} />
      <PageTitleArt title="작품 상세" />
      <Dialog fullScreen open={true} TransitionComponent={Transition} className={useStyles().dialogSize}
      >
        <AppBar className={useStyles().appBar}>
          <Toolbar >
            <IconButton edge="start" aria-label="close">
              <CloseIcon className={useStyles().closeSize} />
            </IconButton>
            <Typography className={useStyles().title}>
              새로운 시작과 만남
              </Typography>
            <Link to={"/resume/modify"}>
              <p className="btn btn-md btn-light btn-square mt-10" >
                EDIT
                </p>
            </Link>
          </Toolbar>
        </AppBar>
        <div className="container" style={{ marginTop: "40px" }}>
          <div className="row">
            <div className="col-md-9 col-sm-9 col-xs-12 xs-mb-50">
              <div className="row">
                <div className="col-md-12 col-sm-12 col-xs-12 mb-20 xs-mb-50">
                  <div className="post">
                    {art.files.length > 0 ? (
                      <Slider {...settings}>
                      </Slider>
                    ) : (
                      <img
                        alt="이미지가 없습니다."
                      />
                    )}

                    <div className="post-info all-padding-20">
                      <h3>
                        {art.title}
                      </h3>
                    </div>
                  </div>
                </div>
                <div className="col-md-12 col-sm-12 col-xs-12 mb-20">
                  <div className="blog-standard">
                    <blockquote>
                      <p>
                        {art.description}
                      </p>
                    </blockquote>
                  </div>

