                    post.slides ? (
                      <div
                        className="col-md-6 col-sm-6 col-xs-12"
                        key={post.id}
                      >
                        <div className="post">
                          <Slider {...settings}>
                            {post.slides.map((image, i) => (
                              <div className="item" key={i}>
                                <img
                                  className="img-responsive"
                                  src={require("../../assets/images/" + image)}
                                  alt=""
                                />
                              </div>
