              </div>
            </div>
            <ArtSidebar resumeId={art.resumeId} name={art.name} selfIntroduce={art.selfIntroduce} category={art.categoryName} />
          </div>
        </div>
      </Dialog>
      <FooterOne />
    </>
  );
}

export default ArtDetail;
