import React from 'react';
import Icofont from 'react-icofont';
import { useSelector, useDispatch } from "react-redux";
import { getArtList } from 'webapp/art/reducer/art.reducer';

const PageList = () => {

  const { pageList, page, start, end, prev, next } = useSelector(state => state.arts.pageResult)

  const dispatch = useDispatch()

  console.log("PageList : " + pageList);

  const movePage = (page) => {
    dispatch(getArtList(page))
  }

  const list = pageList.map(i => (
    <li className="active" key={i} onClick={() => movePage(i)}>
      {i}
    </li>
  ))

  return (
      <div className="row mt-20" style={{ marginBottom: "20px" }}>
        <div className="text-center">
          {prev ? <button className="btn" onClick={() => movePage(start - 1)} >
            <Icofont icon="long-arrow-left" className="mr-5 xs-display-none" />
            PREV
          </button> : <></>}
          {list}
          {next ? <button className="btn" onClick={() => movePage(end + 1)} >
            NEXT
         <Icofont icon="long-arrow-right" className="ml-5 xs-display-none" />
          </button> : <></>}


        </div>
      </div>
  )
}

export default PageList
