import React, { useEffect } from "react";
// DATA Files
import dataNavbar from "webapp/common/data/Navbar/main-navbar-data.json";
// Images
// Components
import HeaderOne from "webapp/common/Header/HeaderOne";
import PageTitleArt from "webapp/art/component/PageTitleArt";
import ArtContent from "webapp/art/component/Detail/ArtContent";
import FooterOne from "webapp/common/Footer/FooterOne";
import { useSelector, useDispatch } from "react-redux";
import { getArtRead } from "webapp/art/reducer/art.reducer";
import { useParams } from "react-router";

import { makeStyles } from '@material-ui/core/styles'
import Dialog from '@material-ui/core/Dialog'
import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar'
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import Slider from "react-slick";
import { Link } from 'react-router-dom'

const ArtDetail = () => {

  const useStyles = makeStyles((theme) => ({
    appBar: {
      position: 'relative',
      backgroundColor: "#561571",
      fontSize: 30,
    },
    title: {
      marginLeft: theme.spacing(3),
      flex: 1,
      color: "white",
      fontSize: 25,
      textAlign: "center"

    },
    closeSize: {
      fontSize: 30,
      color: "white"

    },
    dialogSize: {
      margin: 'auto auto',
      width: '95%',
      verticalAlign: "middle",
    },
  }));

  const settings = {
    dots: false,
    infinite: true,
    centerMode: true,
    autoplay: true,
    autoplaySpeed: 5000,
    slidesToShow: 1,
    slidesToScroll: 1,
    centerPadding: "0",
    className: "blog-grid-slider slick",
  };
  const Transition = React.forwardRef(function Transition(props, ref) {

    return <Slide direction="up" ref={ref} {...props} />;
  });

  const { id } = useParams()
  console.log(id)

  const art = useSelector(state => state.arts.art)
  console.log(art)

  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getArtRead(id))
  }, [])


  return (
    <>
      <HeaderOne data={dataNavbar} />
      <PageTitleArt title="작품 상세" />
      <Dialog fullScreen open={true} TransitionComponent={Transition} className={useStyles().dialogSize}
      >
        <AppBar className={useStyles().appBar}>
          <Toolbar >
            <IconButton edge="start" aria-label="close">
              <CloseIcon className={useStyles().closeSize} />
            </IconButton>
            <Typography className={useStyles().title}>
              새로운 시작과 만남
              </Typography>
            <Link to={"/resume/modify"}>
              <p className="btn btn-md btn-light btn-square mt-10" >
                EDIT
                </p>
            </Link>
          </Toolbar>
        </AppBar>
      </Dialog>
      <FooterOne />
    </>
  );
}

export default ArtDetail;
