import React, { useState } from 'react'
import { changeFileList } from 'webapp/art/reducer/art.reducer'

const ArtFile = ({ uuid, fname }) => {

  const [artFile, setArtFile] = useState({
    title: "",
    detail: "",
    workedDate: "",
    uuid: uuid,
    fname: fname,
    refImg: false
  })

  const artFileChange = e => {
    e.stopPropagation()
    e.preventDefault()

    setArtFile({ ...ArtFile, [e.target.name]: e.target.value })
    console.log(artFile)

    dispatchEvent(changeFileList({ uuid: e.target.getAttribute("data-uuid"), file: { ...artFile }}))
  }

