import React, { useEffect } from "react";
// DATA Files
import dataNavbar from "webapp/common/data/Navbar/main-navbar-data.json";
// Images
// Components
import HeaderOne from "webapp/common/Header/HeaderOne";
import PageTitleArt from "webapp/art/component/PageTitleArt";
import ArtContent from "webapp/art/component/Detail/ArtContent";
import FooterOne from "webapp/common/Footer/FooterOne";
import { useSelector, useDispatch } from "react-redux";
import { getArtRead } from "webapp/art/reducer/art.reducer";
import { useParams } from "react-router";

const ArtDetail = () => {
  const { id } = useParams()
  console.log(id)

  const art = useSelector(state => state.arts.art)
  console.log(art)

  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getArtRead(id))
  }, [])


  return (
    <>
      {/* <HeaderOne data={dataNavbar} />
      <PageTitleArt title="작품 상세" />
      <FooterOne /> */}
      <div>
        <div>
          <div>
            <div className="card col-md-5 offset-md-3">
              <h3 className="text-center">댓글을 수정하세요</h3>
              <div className="card-body">
                <form>
                  <div className="form-group">
                    <label> 게시글 번호 </label>
                    <textarea className="form-control"
                      name="reviewId" readOnly />
                  </div>
                  <div className="form-group">
                    <label> 댓글번호 </label>
                    <textarea className="form-control"
                      name="rno" readOnly />
                  </div>
                  <div className="form-group">
                    <label> 작성자 </label>
                    <textarea className="form-control"
                      name="replyer" readOnly />
                  </div>
                  <div className="form-group">
                    <label> 댓글 내용  </label>
                    <input type='text' placeholder="댓글을 수정하세요" name="text" className="form-control"
                      ></input>
                  </div>
                  <div>
                    <button >수정하기</button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default ArtDetail;
