import React, { useCallback, useEffect, useRef, useState } from 'react'
// DATA Files
import dataNavbar from "webapp/common/data/Navbar/main-navbar-data.json";
// Components
import { HeaderOne, FooterOne } from 'webapp/common'
import PageTitleArt from 'webapp/art/component/PageTitleArt';

import 'webapp/art/style/Art.css'
import { useDispatch, useSelector } from 'react-redux';
import ArtUpload from '../Register/ArtUpload';
import { addFileList, getArtModify, getArtRead, getCategoryList } from 'webapp/art/reducer/art.reducer';
import { useParams } from 'react-router';
import ModifyFile from './ModifyFile';
import ModifyUpload from './ModifyUpload';

const ArtModify = ({ tagline, title, backfont, dash, textBtn, classes }) => {

  const dispatch = useDispatch()

  const { id } = useParams()

  const art = useSelector(state => state.arts.current)
  const fileList = useSelector(state => state.arts.fileList)
  const categories = useSelector(state => state.arts.category)

  const [input, setInput] = useState({
    title: art.title,
    category: { categoryId: art.category.categoryId },
    description: art.description,
    artist: art.artist,
    resume: art.resume,
  })

  useEffect(() => {
    dispatch(getArtRead(id))
    dispatch(getCategoryList())
    art.files.map(file => dispatch(addFileList({uuid: file.uuid, files: file })))
  }, [])

  console.log(categories)

  console.log("art", art)
  console.log("fileList", fileList)
  console.log("files", art.files)

  const handleChange = e => {
    e.stopPropagation()
    e.preventDefault()

    input[e.target.name] = e.target.value
    setInput({ ...input })
  }

  const categoryChange = e => {
    e.stopPropagation()
    e.preventDefault()

    input.category.categoryId = Number(e.target.value)
    setInput({ ...input })
  }

  const ArtModify = e => {
    e.stopPropagation()
    e.preventDefault()

    console.log("fileList", fileList)

    const data = {
      ...input, files: fileList.map(i => {
        console.log("sdkgsdkgsdlkgdslfksdhlf", i)
        return i.file
      })
    }
    dispatch(getArtModify(data))
  }
  console.log(input)

  return (
    <>
      <HeaderOne data={dataNavbar} />
      <PageTitleArt title="작품 수정" />

      <section
        className={"pt-0 pb-0 transition-none " + classes}
        id="contact"
      >
        <div className="col-md-6 col-sm-4 ">
          <div className="pt-50 pb-70 pl-70 pr-70 xs-pt-20 xs-pb-80 ">
            <ModifyUpload {...art.files} />

            {/* {art?.files.map(fileObj => {
              console.log("fileObj", fileObj)
              return (
                <ModifyFile key={fileObj.uuid} {...fileObj} />
              )
            })} */}
          </div>
        </div>
        <div className="container-fluid">
          <div className="col-md-5 col-sm-7 pt-50 pb-70 pl-70 pr-70 xs-pt-20 xs-pb-80">
            <form
              name="art-modify-form"
              id="art-modify-form"
              method="POST"
              className="contact-form-style-02"
              onSubmit={ArtModify}
            >
              <div className="messages"></div>
              <div className="row">
                <div className="col-md-12 col-sm-12">
                  <div className="form-group">
                    <label htmlFor="title" className="dark-color">
                      작품명
                    </label>
                    <input
                      type="text"
                      name="title"
                      className="md-input style-02 input_white"
                      id="title"
                      required
                      data-error="작품명을 입력하세요."
                      value={input.title}
                      onChange={e => handleChange(e)}
                    />
                  </div>
                </div>
                <div className="col-md-12 col-sm-12">
                  <div className="form-group">
                    <label htmlFor="category" className="dark-color">
                      카테고리
                    </label>
                    <select
                      type="number"
                      name="category"
                      className="md-input style-02 sel_arrow input_white"
                      id="category"
                      required
                      data-error="카테고리를 선택해주세요."
                      onChange={e => categoryChange(e)}
                    >
                      <option value={art.category.categoryId}>{art.category.categoryName}</option>
                      {categories.filter(category => category.categoryId !== art.category.categoryId).map(category => {
                        return (
                          <option key={category.categoryId} value={category.categoryId}>{category.categoryName}</option>
                        )
                      })}
                    </select>
                  </div>
                </div>
                <div className="col-md-12 col-sm-12">
                  <div className="form-group">
                    <label htmlFor="description" className="dark-color">
                      설명
                    </label>
                    <textarea
                      name="description"
                      className="md-textarea style-02 input_white"
                      id="description"
                      rows="7"
                      required
                      data-error="작품에 대한 설명을 입력해주세요."
                      value={input.description}
                      onChange={e => handleChange(e)}
                    />
                  </div>
                </div>
                <div className="col-md-12 col-sm-12">
                  <div className="text-center mt-20">
                    <button
                      type="submit"
                      name="submit"
                      className="btn btn-xl btn-color btn-square remove-margin"
                      style={{ width: "50%" }}
                    >
                      수정하기
                    </button>
                  </div>
                </div>
              </div>
            </form>
          </div>
        </div>
      </section>

      <FooterOne />
    </>
  )
}

export default ArtModify
