import React, { useEffect } from "react";
// DATA Files
import dataNavbar from "webapp/common/data/Navbar/main-navbar-data.json";
// Images
// Components
import HeaderOne from "webapp/common/Header/HeaderOne";
import PageTitleArt from "webapp/art/component/PageTitleArt";
import ArtContent from "webapp/art/component/Detail/ArtContent";
import FooterOne from "webapp/common/Footer/FooterOne";
import { useSelector, useDispatch } from "react-redux";
import { getArtRead } from "webapp/art/reducer/art.reducer";
import { useParams } from "react-router";

import { makeStyles } from '@material-ui/core/styles'
import Dialog from '@material-ui/core/Dialog'
import AppBar from '@material-ui/core/AppBar'
import ToolBar from '@material-ui/core/ToolBar'
import Slide from '@material-ui/core/Slide'

const ArtDetail = () => {

  const useStyles = makeStyles((theme) => ({
    appBar: {
      position: 'relative',
      backgroundColor: "#561571",
      fontSize: 30,
    },
    title: {
      marginLeft: theme.spacing(3),
      flex: 1,
      color: "white",
      fontSize: 25,
      textAlign: "center"

    },
    closeSize: {
      fontSize: 30,
      color: "white"

    },
    dialogSize: {
      margin: 'auto auto',
      width: '95%',
      verticalAlign: "middle",
    },
  }));

  const settings = {
    dots: false,
    infinite: true,
    centerMode: true,
    autoplay: true,
    autoplaySpeed: 5000,
    slidesToShow: 1,
    slidesToScroll: 1,
    centerPadding: "0",
    className: "blog-grid-slider slick",
  };
  const Transition = React.forwardRef(function Transition(props, ref) {

    return <Slide direction="up" ref={ref} {...props} />;
  });

  const { id } = useParams()
  console.log(id)

  const art = useSelector(state => state.arts.art)
  console.log(art)

  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getArtRead(id))
  }, [])


  return (
    <>
      <HeaderOne data={dataNavbar} />
      <PageTitleArt title="작품 상세" />
      <Dialog open={true} className={useStyles().dialogSize}>
        <AppBar className={useStyles().appBar}>
          <ToolBar>

          </ToolBar>
        </AppBar>
        </Dialog>
      <FooterOne />
    </>
  );
}

export default ArtDetail;
